mapper.select:
SELECT u.id AS userId, u.username AS username, u.email AS email, u.age AS age, u.created_at AS createdAt,
p.id AS profileId, p.full_name AS profileFullName, p.address AS profileAddress,
r.id AS roleId, r.name AS roleName FROM users u LEFT JOIN profiles p ON u.id = p.user_id LEFT JOIN user_roles ur ON u.id = ur.user_id LEFT JOIN roles r ON ur.role_id = r.id WHERE u.id = #{id}

mapper.update:
UPDATE users
SET username = #{username},
    email = #{email},
    age = #{age}
WHERE id = #{id}
